// Lightweight fallback Sentiment.js (offline mode)
class Sentiment {
  analyze(text) {
    const negative = ["sad", "angry", "bad", "hate", "upset", "tired", "lonely"];
    const positive = ["happy", "great", "love", "excited", "good", "awesome", "amazing"];

    let score = 0;
    text = text.toLowerCase();

    negative.forEach(word => { if (text.includes(word)) score -= 2; });
    positive.forEach(word => { if (text.includes(word)) score += 2; });

    return { score };
  }
}

